    document.head.appendChild(styles);
}

function addCleanIntersectionObserver() {
    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(function(entry) {
            if (entry.isIntersecting) {
                entry.target.classList.add('animate');
            }
        });
    }, { 
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    });

    setTimeout(function() {
        const header = document.getElementById('pflegebox-clean-header');
        if (header) {
            observer.observe(header);
        }

        const cleanItems = document.querySelectorAll('.pflegebox-clean-item');
        cleanItems.forEach(function(item, index) {
            item.style.transitionDelay = (0.2 + (index * 0.15)) + 's';
            observer.observe(item);
        });

        console.log('‚ú® Pflegebox Clean Intersection Observer attivato!');
    }, 100);
}

