function addKKBonusEventListeners() {
    const buttons = document.querySelectorAll('.kk-btn');
    const answers = document.querySelectorAll('.kk-answer');

    if (!buttons.length) return;

    buttons.forEach(function(button) {
        button.addEventListener('click', function() {
            const answer = this.getAttribute('data-answer');

            // Nascondi tutte le risposte
            answers.forEach(function(ans) { ans.style.display = 'none'; });

            // Reset Button Opacity
            buttons.forEach(function(btn) { btn.style.opacity = '0.7'; });

            // Evidenzia pulsante selezionato
            this.style.opacity = '1';

            // Mostra la risposta corrispondente
            const answerClass = '.kk-answer-' + answer;
            const answerElement = document.querySelector(answerClass);
            if (answerElement) {
                answerElement.style.display = 'block';
                setTimeout(function() {
                    answerElement.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                }, 100);
            }
        });
    });
}

// Funzione per forzare il download del PDF
function downloadPDF(event) {
    event.preventDefault();

    const pdfUrl = 'https://cdn.shopify.com/s/files/1/0909/2805/4653/files/Bestellformular_Pflegebox.pdf?v=1760449576';
    const fileName = 'Bestellformular_Pflegebox.pdf';

    fetch(pdfUrl)
        .then(response => response.blob())
        .then(blob => {
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.style.display = 'none';
            a.href = url;
            a.download = fileName;
            document.body.appendChild(a);
            a.click();
            window.URL.revokeObjectURL(url);
            document.body.removeChild(a);
        })
        .catch(error => {
            console.error('Errore nel download:', error);
            // Fallback: apri in una nuova tab
            window.open(pdfUrl, '_blank');
        });
}

